        job
            .on("complete", () => console.log(`Notification job ${job.id} completed`))
            .on("failed", (err) => console.log(`Notification job ${newJob.id} failed: ${err}`))
            .on("progress", (progress) => console.log(`Notification job ${job.id} ${progress}% complete`));
