import { createPushNotificationsJobs } from "./8-job";
import kue from "kue";

/**
 * Import the function createPushNotificationsJobs
 * Create a queue with kue
 * write a test suite for the function createPushNotificationsJobs
 * use queue.testMode to validate which jobs are inside the queue
 */
describe("createPushNotificationsJobs", () => {
  let queue;

  beforeAll(() => {
   	queue = kue.createQueue({ redis: 'resid://localhost:6379'});
   	queue.testMode.enter();
  });

  afterAll(() => {
 		queue.testMode.clear();
   	queue.testMode.exit();
  });

  it("display an error message if jobs is not an array", () => {
    const list = "hello";
    expect(() => {
    	createPushNotificationsJobs(list, queue);
    }).toThrow("Jobs is not an array");
    });
		});
    it("create two new jobs to the queue", () => {
        const list = [
        {
            phoneNumber: "4153518780",
            message: "This is the code 1234 to verify your account",
        },
        ];
        createPushNotificationsJobs(list, queue);
        expect(queue.testMode.jobs.length).toBe(1);
        expect(queue.testMode.jobs[0].type).toBe("push_notification_code_3");
        expect(queue.testMode.jobs[0].data).toEqual(list[0]);
});

